import { apiRequest } from "@/lib/queryClient";
import { Hostel, Room } from "@shared/schema";
import { Loader2, Plus, Building, Bed, Wrench } from "lucide-react";

const hostelSchema = z.object({
  name: z.string().min(1, "Name is required"),
  gender: z.enum(["male", "female", "other"]),
  totalCapacity: z.coerce.number().min(1, "Capacity must be at least 1"),
});

const roomSchema = z.object({
  floorId: z.string().min(1, "Floor is required"),
  roomNumber: z.string().min(1, "Room number is required"),
  capacity: z.coerce.number().min(1, "Capacity must be at least 1"),
});

type HostelFormData = z.infer<typeof hostelSchema>;
type RoomFormData = z.infer<typeof roomSchema>;

export default function HostelManagement() {
  const [hostelDialogOpen, setHostelDialogOpen] = useState(false);
  const [roomDialogOpen, setRoomDialogOpen] = useState(false);
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const { data: hostelsData, isLoading: hostelsLoading } = useQuery<{ hostels: Hostel[] }>({
    queryKey: ["/api/hostels"],
  });

  const { data: roomsData, isLoading: roomsLoading } = useQuery<{ rooms: any[] }>({
    queryKey: ["/api/rooms"],
  });

  const hostelForm = useForm<HostelFormData>({
    resolver: zodResolver(hostelSchema),
    defaultValues: {
      name: "",
      gender: "male",
      totalCapacity: 100,
    },
  });

  const roomForm = useForm<RoomFormData>({
    resolver: zodResolver(roomSchema),
    defaultValues: {
      floorId: "",
      roomNumber: "",
      capacity: 2,
    },
  });

  const createHostelMutation = useMutation({
    mutationFn: async (data: HostelFormData) => {
      const res = await apiRequest("POST", "/api/hostels", data);
      return res.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/hostels"] });
      setHostelDialogOpen(false);
      hostelForm.reset();
      toast({
        title: "Hostel created",
        description: "New hostel has been created successfully.",
      });
    },
    onError: (error: Error) => {
      toast({
        title: "Creation failed",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  const createRoomMutation = useMutation({
    mutationFn: async (data: RoomFormData) => {
      const res = await apiRequest("POST", "/api/rooms", data);
      return res.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/rooms"] });
      setRoomDialogOpen(false);
      roomForm.reset();
      toast({
        title: "Room created",
        description: "New room has been created successfully.",
      });
    },
    onError: (error: Error) => {
      toast({
        title: "Creation failed",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  const toggleMaintenanceMutation = useMutation({
    mutationFn: async ({ roomId, isUnderMaintenance }: { roomId: string; isUnderMaintenance: boolean }) => {
      const res = await apiRequest("PATCH", `/api/rooms/${roomId}`, { isUnderMaintenance });
      return res.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/rooms"] });
      toast({
        title: "Room updated",
        description: "Room maintenance status has been updated.",
      });
    },
    onError: (error: Error) => {
      toast({
        title: "Update failed",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  const hostels = hostelsData?.hostels || [];
  const rooms = roomsData?.rooms || [];

  const totalRooms = rooms.length;
  const occupiedRooms = rooms.filter((r: any) => r.occupiedCount > 0).length;
  const maintenanceRooms = rooms.filter((r: any) => r.isUnderMaintenance).length;

  return (
    <div className="max-w-7xl mx-auto p-6 space-y-6">
      <div>
        <h1 className="text-3xl font-bold">Hostel Management</h1>
        <p className="text-muted-foreground mt-1">Manage hostels, wings, floors, and rooms</p>
      </div>

      <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
        <Card>
          <CardHeader className="pb-3">
            <CardTitle className="text-sm font-medium">Total Hostels</CardTitle>
          </CardHeader>
          <CardContent>
            <div className="flex items-center gap-2">
              <Building className="w-5 h-5 text-muted-foreground" />
              <span className="text-2xl font-bold">{hostels.length}</span>
            </div>
          </CardContent>
        </Card>

        <Card>
          <CardHeader className="pb-3">
            <CardTitle className="text-sm font-medium">Total Rooms</CardTitle>
          </CardHeader>
          <CardContent>
            <div className="flex items-center gap-2">
              <Bed className="w-5 h-5 text-muted-foreground" />
              <span className="text-2xl font-bold">{totalRooms}</span>
            </div>
          </CardContent>
        </Card>

        <Card>
          <CardHeader className="pb-3">
            <CardTitle className="text-sm font-medium">Occupied Rooms</CardTitle>
          </CardHeader>
          <CardContent>
            <span className="text-2xl font-bold">{occupiedRooms}</span>
            <p className="text-xs text-muted-foreground mt-1">
              {totalRooms > 0 ? Math.round((occupiedRooms / totalRooms) * 100) : 0}% occupancy
            </p>
          </CardContent>
        </Card>

        <Card>
          <CardHeader className="pb-3">
            <CardTitle className="text-sm font-medium">Under Maintenance</CardTitle>
          </CardHeader>
          <CardContent>
            <div className="flex items-center gap-2">
              <Wrench className="w-5 h-5 text-muted-foreground" />
              <span className="text-2xl font-bold">{maintenanceRooms}</span>
            </div>
          </CardContent>
        </Card>
      </div>

      <Tabs defaultValue="hostels" className="space-y-6">
        <TabsList>
          <TabsTrigger value="hostels">Hostels</TabsTrigger>
          <TabsTrigger value="rooms">Rooms</TabsTrigger>
        </TabsList>

        <TabsContent value="hostels">
          <Card>
            <CardHeader>
              <div className="flex items-center justify-between">
                <div>
                  <CardTitle>Hostels</CardTitle>
                  <CardDescription>Manage hostel buildings</CardDescription>
                </div>
                <Dialog open={hostelDialogOpen} onOpenChange={setHostelDialogOpen}>
                  <DialogTrigger asChild>
                    <Button>
                      <Plus className="w-4 h-4 mr-2" />
                      Add Hostel
                    </Button>
                  </DialogTrigger>
                  <DialogContent>
                    <DialogHeader>
                      <DialogTitle>Create New Hostel</DialogTitle>
                      <DialogDescription>Add a new hostel building to the system</DialogDescription>
                    </DialogHeader>
                    <Form {...hostelForm}>
                      <form onSubmit={hostelForm.handleSubmit((data) => createHostelMutation.mutate(data))} className="space-y-4">
                        <FormField
                          control={hostelForm.control}
                          name="name"
                          render={({ field }) => (
                            <FormItem>
                              <FormLabel>Hostel Name</FormLabel>
                              <FormControl>
                                <Input placeholder="e.g., Hostel A" {...field} />
                              </FormControl>
                              <FormMessage />
                            </FormItem>
                          )}
                        />

                        <FormField
                          control={hostelForm.control}
                          name="gender"
                          render={({ field }) => (
                            <FormItem>
                              <FormLabel>Gender</FormLabel>
                              <Select onValueChange={field.onChange} defaultValue={field.value}>
                                <FormControl>
                                  <SelectTrigger>
                                    <SelectValue placeholder="Select gender" />
                                  </SelectTrigger>
                                </FormControl>
                                <SelectContent>
                                  <SelectItem value="male">Male</SelectItem>
                                  <SelectItem value="female">Female</SelectItem>
                                  <SelectItem value="other">Other</SelectItem>
                                </SelectContent>
                              </Select>
                              <FormMessage />
                            </FormItem>
                          )}
                        />

                        <FormField
                          control={hostelForm.control}
                          name="totalCapacity"
                          render={({ field }) => (
                            <FormItem>
                              <FormLabel>Total Capacity</FormLabel>
                              <FormControl>
                                <Input type="number" {...field} />
                              </FormControl>
                              <FormMessage />
                            </FormItem>
                          )}
                        />

                        <DialogFooter>
                          <Button type="submit" disabled={createHostelMutation.isPending}>
                            {createHostelMutation.isPending && <Loader2 className="w-4 h-4 mr-2 animate-spin" />}
                            Create Hostel
                          </Button>
                        </DialogFooter>
                      </form>
                    </Form>
                  </DialogContent>
                </Dialog>
              </div>
            </CardHeader>
            <CardContent>
              {hostelsLoading ? (
                <div className="flex items-center justify-center py-12">
                  <Loader2 className="w-8 h-8 animate-spin" />
                </div>
              ) : hostels.length === 0 ? (
                <p className="text-center text-muted-foreground py-12">No hostels found</p>
              ) : (
                <Table>
                  <TableHeader>
                    <TableRow>
                      <TableHead>Name</TableHead>
                      <TableHead>Gender</TableHead>
                      <TableHead>Capacity</TableHead>
                      <TableHead>Occupied</TableHead>
                      <TableHead>Occupancy</TableHead>
                    </TableRow>
                  </TableHeader>
                  <TableBody>
                    {hostels.map((hostel) => (
                      <TableRow key={hostel.id}>
                        <TableCell className="font-medium">{hostel.name}</TableCell>
                        <TableCell>
                          <Badge variant="outline" className="capitalize">{hostel.gender}</Badge>
                        </TableCell>
                        <TableCell>{hostel.totalCapacity}</TableCell>
                        <TableCell>{hostel.occupiedCapacity}</TableCell>
                        <TableCell>
                          <div className="flex items-center gap-2">
                            <div className="w-24 h-2 bg-muted rounded-full overflow-hidden">
                              <div
                                className="h-full bg-primary"
                                style={{ width: `${(hostel.occupiedCapacity / hostel.totalCapacity) * 100}%` }}
                              />
                            </div>
                            <span className="text-sm text-muted-foreground">
                              {Math.round((hostel.occupiedCapacity / hostel.totalCapacity) * 100)}%
                            </span>
                          </div>
                        </TableCell>
                      </TableRow>
                    ))}
                  </TableBody>
                </Table>
              )}
            </CardContent>
          </Card>
        </TabsContent>

        <TabsContent value="rooms">
          <Card>
            <CardHeader>
              <div className="flex items-center justify-between">
                <div>
                  <CardTitle>Rooms</CardTitle>
                  <CardDescription>Manage individual rooms</CardDescription>
                </div>
              </div>
            </CardHeader>
            <CardContent>
              {roomsLoading ? (
                <div className="flex items-center justify-center py-12">
                  <Loader2 className="w-8 h-8 animate-spin" />
                </div>
              ) : rooms.length === 0 ? (
                <p className="text-center text-muted-foreground py-12">No rooms found</p>
              ) : (
                <Table>
                  <TableHeader>
                    <TableRow>
                      <TableHead>Room Number</TableHead>
                      <TableHead>Capacity</TableHead>
                      <TableHead>Occupied</TableHead>
                      <TableHead>Status</TableHead>
                      <TableHead>Maintenance</TableHead>
                    </TableRow>
                  </TableHeader>
                  <TableBody>
                    {rooms.map((room: any) => (
                      <TableRow key={room.id}>
                        <TableCell className="font-medium">{room.roomNumber}</TableCell>
                        <TableCell>{room.capacity}</TableCell>
                        <TableCell>{room.occupiedCount}</TableCell>
                        <TableCell>
                          {room.isUnderMaintenance ? (
                            <Badge variant="destructive">Under Maintenance</Badge>
                          ) : room.occupiedCount >= room.capacity ? (
                            <Badge variant="secondary">Full</Badge>
                          ) : (
                            <Badge variant="default">Available</Badge>
                          )}
                        </TableCell>
                        <TableCell>
                          <Switch
                            checked={room.isUnderMaintenance}
                            onCheckedChange={(checked) => 
                              toggleMaintenanceMutation.mutate({ 
                                roomId: room.id, 
                                isUnderMaintenance: checked 
                              })
                            }
                            disabled={toggleMaintenanceMutation.isPending}
                          />
                        </TableCell>
                      </TableRow>
                    ))}
                  </TableBody>
                </Table>
              )}
            </CardContent>
          </Card>
        </TabsContent>
      </Tabs>
    </div>
  );
}

client/src/pages/MessChangeRequest.tsx.content:
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { z } from "zod";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Textarea } from "@/components/ui/textarea";
import { Badge } from "@/components/ui/badge";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Form, FormControl, FormField, FormItem, FormLabel, FormMessage } from "@/components/ui/form";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import { MessChangeRequest, Mess, User } from "@shared/schema";
import { Loader2, Send, Utensils } from "lucide-react";
import { formatDistanceToNow } from "date-fns";

const messChangeSchema = z.object({
  newMessId: z.string().min(1, "Please select a mess"),
  reason: z.string().optional(),
});

type MessChangeFormData = z.infer<typeof messChangeSchema>;

export default function MessChangeRequestPage() {
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const { data: userData } = useQuery<{ user: User }>({
    queryKey: ["/api/auth/me"],
  });

  const { data: messesData, isLoading: messesLoading } = useQuery<{ messes: Mess[] }>({
    queryKey: ["/api/messes"],
  });

  const { data: requestsData, isLoading: requestsLoading } = useQuery<{ requests: MessChangeRequest[] }>({
    queryKey: ["/api/mess-changes"],
  });

  const form = useForm<MessChangeFormData>({
    resolver: zodResolver(messChangeSchema),
    defaultValues: {
      newMessId: "",
      reason: "",
    },
  });

  const createMutation = useMutation({
    mutationFn: async (data: MessChangeFormData) => {
      const res = await apiRequest("POST", "/api/mess-changes", data);
      return res.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/mess-changes"] });
      form.reset();
      toast({
        title: "Request submitted",
        description: "Your mess change request has been submitted successfully.",
      });
    },
    onError: (error: Error) => {
      toast({
        title: "Submission failed",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  const onSubmit = (data: MessChangeFormData) => {
    createMutation.mutate(data);
  };

  const messes = messesData?.messes || [];
  const requests = requestsData?.requests || [];
  const pendingRequests = requests.filter(r => r.status === "pending");
  const processedRequests = requests.filter(r => r.status !== "pending");

  const getStatusBadge = (status: string) => {
    if (status === "pending") return <Badge variant="outline">Pending</Badge>;
    if (status === "approved") return <Badge variant="default">Approved</Badge>;
    return <Badge variant="destructive">Rejected</Badge>;
  };

  const currentMessId = requests.length > 0 
    ? requests.filter(r => r.status === "approved")[requests.filter(r => r.status === "approved").length - 1]?.newMessId || requests[0]?.currentMessId
    : undefined;

  const currentMess = messes.find(m => m.id === currentMessId);

  const availableMesses = messes.filter(m => m.id !== currentMessId);

  return (
    <div className="max-w-4xl mx-auto p-6 space-y-6">
      <div>
        <h1 className="text-3xl font-bold">Mess Change Request</h1>
        <p className="text-muted-foreground mt-1">Request to change your mess allotment</p>
      </div>

      {currentMess && (
        <Card>
          <CardHeader>
            <CardTitle>Current Mess</CardTitle>
          </CardHeader>
          <CardContent>
            <div className="flex items-center gap-4">
              <Utensils className="w-8 h-8 text-primary" />
              <div>
                <p className="text-xl font-semibold">{currentMess.name}</p>
                <p className="text-sm text-muted-foreground">
                  Capacity: {currentMess.currentCount}/{currentMess.capacity}
                </p>
              </div>
            </div>
          </CardContent>
        </Card>
      )}

      <Card>
        <CardHeader>
          <CardTitle>Submit New Request</CardTitle>
          <CardDescription>Select a new mess and provide a reason for the change</CardDescription>
        </CardHeader>
        <CardContent>
          {messesLoading ? (
            <div className="flex items-center justify-center py-12">
              <Loader2 className="w-8 h-8 animate-spin" />
            </div>
          ) : (
            <Form {...form}>
              <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-4">
                <FormField
                  control={form.control}
                  name="newMessId"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>New Mess</FormLabel>
                      <Select
                        onValueChange={field.onChange}
                        defaultValue={field.value}
                        disabled={createMutation.isPending}
                      >
                        <FormControl>
                          <SelectTrigger>
                            <SelectValue placeholder="Select a mess" />
                          </SelectTrigger>
                        </FormControl>
                        <SelectContent>
                          {availableMesses.map((mess) => (
                            <SelectItem key={mess.id} value={mess.id}>
                              {mess.name} ({mess.currentCount}/{mess.capacity})
                            </SelectItem>
                          ))}
                        </SelectContent>
                      </Select>
                      <FormMessage />
                    </FormItem>
                  )}
                />

                <FormField
                  control={form.control}
                  name="reason"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Reason (Optional)</FormLabel>
                      <FormControl>
                        <Textarea
                          placeholder="Explain why you want to change mess..."
                          {...field}
                          disabled={createMutation.isPending}
                        />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />

                <Button type="submit" disabled={createMutation.isPending}>
                  {createMutation.isPending ? (
                    <Loader2 className="w-4 h-4 mr-2 animate-spin" />
                  ) : (
                    <Send className="w-4 h-4 mr-2" />
                  )}
                  Submit Request
                </Button>
              </form>
            </Form>
          )}
        </CardContent>
      </Card>

      {requestsLoading ? (
        <div className="flex items-center justify-center py-12">
          <Loader2 className="w-8 h-8 animate-spin" />
        </div>
      ) : (
        <>
          {pendingRequests.length > 0 && (
            <Card>
              <CardHeader>
                <CardTitle>Pending Requests</CardTitle>
                <CardDescription>Mess change requests awaiting approval</CardDescription>
              </CardHeader>
              <CardContent className="space-y-4">
                {pendingRequests.map((request) => {
                  const newMess = messes.find(m => m.id === request.newMessId);
                  return (
                    <div key={request.id} className="border rounded-lg p-4">
                      <div className="flex items-center justify-between mb-2">
                        <div className="flex items-center gap-2">
                          <Utensils className="w-4 h-4" />
                          <span className="font-medium">Change to: {newMess?.name}</span>
                        </div>
                        {getStatusBadge(request.status)}
                      </div>
                      {request.reason && (
                        <p className="text-sm text-muted-foreground mb-2">{request.reason}</p>
                      )}
                      <p className="text-xs text-muted-foreground">
                        Submitted {formatDistanceToNow(new Date(request.createdAt), { addSuffix: true })}
                      </p>
                    </div>
                  );
                })}
              </CardContent>
            </Card>
          )}

          {processedRequests.length > 0 && (
            <Card>
              <CardHeader>
                <CardTitle>Request History</CardTitle>
                <CardDescription>Previously processed requests</CardDescription>
              </CardHeader>
              <CardContent className="space-y-4">
                {processedRequests.map((request) => {
                  const newMess = messes.find(m => m.id === request.newMessId);
                  return (
                    <div key={request.id} className="border rounded-lg p-4">
                      <div className="flex items-center justify-between mb-2">
                        <div className="flex items-center gap-2">
                          <Utensils className="w-4 h-4" />
                          <span className="font-medium">Change to: {newMess?.name}</span>
                        </div>
                        {getStatusBadge(request.status)}
                      </div>
                      {request.reason && (
                        <p className="text-sm text-muted-foreground mb-2">{request.reason}</p>
                      )}
                      <p className="text-xs text-muted-foreground">
                        Submitted {formatDistanceToNow(new Date(request.createdAt), { addSuffix: true })}
                      </p>
                    </div>
                  );
                })}
              </CardContent>
            </Card>
          )}
        </>
      )}
    </div>
  );
}

client/src/pages/Profile.tsx.content:
import { useState } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { z } from "zod";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import { User } from "@shared/schema";
import { Loader2, Edit2, Save, X } from "lucide-react";

const profileUpdateSchema = z.object({
  name: z.string().min(1, "Name is required"),
  phone: z.string().optional(),
});

type ProfileUpdateData = z.infer<typeof profileUpdateSchema>;

export default function Profile() {
  const [isEditing, setIsEditing] = useState(false);
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const { data: userData, isLoading } = useQuery<{ user: User }>({
    queryKey: ["/api/auth/me"],
  });

  const form = useForm<ProfileUpdateData>({
    resolver: zodResolver(profileUpdateSchema),
    values: {
      name: userData?.user?.name || "",
      phone: userData?.user?.phone || "",
    },
  });

  const updateMutation = useMutation({
    mutationFn: async (data: ProfileUpdateData) => {
      if (!userData?.user?.id) throw new Error("User ID not found");
      const res = await apiRequest("PATCH", `/api/students/${userData.user.id}`, data);
      return res.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/auth/me"] });
      setIsEditing(false);
      toast({
        title: "Profile updated",
        description: "Your profile has been updated successfully.",
      });
    },
    onError: (error: Error) => {
      toast({
        title: "Update failed",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  const onSubmit = (data: ProfileUpdateData) => {
    updateMutation.mutate(data);
  };

  if (isLoading) {
    return (
      <div className="flex items-center justify-center h-full">
        <Loader2 className="w-8 h-8 animate-spin" />
      </div>
    );
  }

  const user = userData?.user;

  return (
    <div className="max-w-4xl mx-auto p-6 space-y-6">
      <div>
        <h1 className="text-3xl font-bold">Profile</h1>
        <p className="text-muted-foreground mt-1">Manage your personal information</p>
      </div>

      <Card>
        <CardHeader>
          <div className="flex items-center justify-between">
            <div>
              <CardTitle>Personal Information</CardTitle>
              <CardDescription>Your account details</CardDescription>
            </div>
            {!isEditing && (
              <Button variant="outline" size="sm" onClick={() => setIsEditing(true)}>
                <Edit2 className="w-4 h-4 mr-2" />
                Edit
              </Button>
            )}
          </div>
        </CardHeader>
        <CardContent>
          {isEditing ? (
            <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-4">
              <div className="space-y-2">
                <Label htmlFor="name">Name</Label>
                <Input
                  id="name"
                  {...form.register("name")}
                  disabled={updateMutation.isPending}
                />
                {form.formState.errors.name && (
                  <p className="text-sm text-destructive">{form.formState.errors.name.message}</p>
                )}
              </div>

              <div className="space-y-2">
                <Label htmlFor="phone">Phone</Label>
                <Input
                  id="phone"
                  {...form.register("phone")}
                  disabled={updateMutation.isPending}
                />
              </div>

              <div className="flex gap-2">
                <Button type="submit" disabled={updateMutation.isPending}>
                  {updateMutation.isPending ? (
                    <Loader2 className="w-4 h-4 mr-2 animate-spin" />
                  ) : (
                    <Save className="w-4 h-4 mr-2" />
                  )}
                  Save Changes
                </Button>
                <Button
                  type="button"
                  variant="outline"
                  onClick={() => {
                    setIsEditing(false);
                    form.reset();
                  }}
                  disabled={updateMutation.isPending}
                >
                  <X className="w-4 h-4 mr-2" />
                  Cancel
                </Button>
              </div>
            </form>
          ) : (
            <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
              <div>
                <Label className="text-muted-foreground">Name</Label>
                <p className="text-lg font-medium">{user?.name}</p>
              </div>
              <div>
                <Label className="text-muted-foreground">Email</Label>
                <p className="text-lg font-medium">{user?.email}</p>
              </div>
              <div>
                <Label className="text-muted-foreground">Department</Label>
                <p className="text-lg font-medium">{user?.department || "N/A"}</p>
              </div>
              <div>
                <Label className="text-muted-foreground">Year</Label>
                <p className="text-lg font-medium">{user?.year || "N/A"}</p>
              </div>
              <div>
                <Label className="text-muted-foreground">GPA</Label>
                <p className="text-lg font-medium">{user?.gpa ? (user.gpa / 10).toFixed(2) : "N/A"}</p>
              </div>
              <div>
                <Label className="text-muted-foreground">Phone</Label>
                <p className="text-lg font-medium">{user?.phone || "N/A"}</p>
              </div>
              <div>
                <Label className="text-muted-foreground">Gender</Label>
                <p className="text-lg font-medium capitalize">{user?.gender || "N/A"}</p>
              </div>
              <div>
                <Label className="text-muted-foreground">Role</Label>
                <p className="text-lg font-medium capitalize">{user?.role}</p>
              </div>
            </div>
          )}
        </CardContent>
      </Card>
    </div>
  );
}

client/src/pages/StudentManagement.tsx.content:
import { useState, useMemo } from "react";
import { useQuery } from "@tanstack/react-query";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Input } from "@/components/ui/input";
import { Badge } from "@/components/ui/badge";
import { Table, TableBody, TableCell, TableHead, TableHeader, TableRow } from "@/components/ui/table";
import { User } from "@shared/schema";
import { Loader2, Search, Users } from "lucide-react";

export default function StudentManagement() {
  const [searchQuery, setSearchQuery] = useState("");

  const { data: studentsData, isLoading } = useQuery<{ students: User[] }>({
    queryKey: ["/api/students"],
  });

  const students = studentsData?.students || [];

  const filteredStudents = useMemo(() => {
    if (!searchQuery) return students;

    const query = searchQuery.toLowerCase();
    return students.filter(
      (student) =>
        student.name?.toLowerCase().includes(query) ||
        student.email?.toLowerCase().includes(query) ||
        student.department?.toLowerCase().includes(query)
    );
  }, [students, searchQuery]);

  const totalStudents = students.length;
  const avgGpa = students.length > 0
    ? students.reduce((sum, s) => sum + (s.gpa || 0), 0) / students.length / 10
    : 0;

  return (
    <div className="max-w-7xl mx-auto p-6 space-y-6">
      <div>
        <h1 className="text-3xl font-bold">Student Management</h1>
        <p className="text-muted-foreground mt-1">View and manage all students</p>
      </div>

      <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
        <Card>
          <CardHeader className="pb-3">
            <CardTitle className="text-sm font-medium">Total Students</CardTitle>
          </CardHeader>
          <CardContent>
            <div className="flex items-center gap-2">
              <Users className="w-5 h-5 text-muted-foreground" />
              <span className="text-2xl font-bold">{totalStudents}</span>
            </div>
          </CardContent>
        </Card>

        <Card>
          <CardHeader className="pb-3">
            <CardTitle className="text-sm font-medium">Average GPA</CardTitle>
          </CardHeader>
          <CardContent>
            <span className="text-2xl font-bold">{avgGpa.toFixed(2)}</span>
          </CardContent>
        </Card>

        <Card>
          <CardHeader className="pb-3">
            <CardTitle className="text-sm font-medium">Filtered Results</CardTitle>
          </CardHeader>
          <CardContent>
            <span className="text-2xl font-bold">{filteredStudents.length}</span>
          </CardContent>
        </Card>
      </div>

      <Card>
        <CardHeader>
          <CardTitle>Students List</CardTitle>
          <CardDescription>All registered students</CardDescription>
          <div className="relative mt-4">
            <Search className="absolute left-3 top-1/2 -translate-y-1/2 w-4 h-4 text-muted-foreground" />
            <Input
              placeholder="Search by name, email, or department..."
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
              className="pl-9"
            />
          </div>
        </CardHeader>
        <CardContent>
          {isLoading ? (
            <div className="flex items-center justify-center py-12">
              <Loader2 className="w-8 h-8 animate-spin" />
            </div>
          ) : filteredStudents.length === 0 ? (
            <p className="text-center text-muted-foreground py-12">No students found</p>
          ) : (
            <Table>
              <TableHeader>
                <TableRow>
                  <TableHead>Name</TableHead>
                  <TableHead>Email</TableHead>
                  <TableHead>Department</TableHead>
                  <TableHead>Year</TableHead>
                  <TableHead>GPA</TableHead>
                  <TableHead>Phone</TableHead>
                  <TableHead>Gender</TableHead>
                </TableRow>
              </TableHeader>
              <TableBody>
                {filteredStudents.map((student) => (
                  <TableRow key={student.id}>
                    <TableCell className="font-medium">{student.name}</TableCell>
                    <TableCell>{student.email}</TableCell>
                    <TableCell>
                      {student.department ? (
                        <Badge variant="outline">{student.department}</Badge>
                      ) : (
                        "N/A"
                      )}
                    </TableCell>
                    <TableCell>{student.year || "N/A"}</TableCell>
                    <TableCell>
                      {student.gpa ? (student.gpa / 10).toFixed(2) : "N/A"}
                    </TableCell>
                    <TableCell>{student.phone || "N/A"}</TableCell>
                    <TableCell>
                      {student.gender ? (
                        <Badge variant="secondary" className="capitalize">
                          {student.gender}
                        </Badge>
                      ) : (
                        "N/A"
                      )}
                    </TableCell>
                  </TableRow>
                ))}
              </TableBody>
            </Table>
          )}
        </CardContent>
      </Card>
    </div>
  );
}

client/src/pages/ApprovalsManagement.tsx.content:
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { Table, TableBody, TableCell, TableHead, TableHeader, TableRow } from "@/components/ui/table";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import { RoomSwapRequest, LeaveApplication, MessChangeRequest } from "@shared/schema";
import { Loader2, Check, X } from "lucide-react";
import { format, formatDistanceToNow } from "date-fns";

export default function ApprovalsManagement() {
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const { data: roomSwapsData, isLoading: roomSwapsLoading } = useQuery<{ requests: RoomSwapRequest[] }>({
    queryKey: ["/api/room-swaps"],
  });

  const { data: leaveAppsData, isLoading: leaveAppsLoading } = useQuery<{ applications: LeaveApplication[] }>({
    queryKey: ["/api/leave-applications"],
  });

  const { data: messChangesData, isLoading: messChangesLoading } = useQuery<{ requests: MessChangeRequest[] }>({
    queryKey: ["/api/mess-changes"],
  });

  const approveRoomSwapMutation = useMutation({
    mutationFn: async ({ id, approved }: { id: string; approved: boolean }) => {
      const res = await apiRequest("POST", `/api/room-swaps/${id}/approve`, { approved });
      return res.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/room-swaps"] });
      toast({
        title: "Request processed",
        description: "Room swap request has been processed.",
      });
    },
    onError: (error: Error) => {
      toast({
        title: "Processing failed",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  const approveLeaveAppMutation = useMutation({
    mutationFn: async ({ id, approved }: { id: string; approved: boolean }) => {
      const res = await apiRequest("POST", `/api/leave-applications/${id}/approve`, { approved });
      return res.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/leave-applications"] });
      toast({
        title: "Application processed",
        description: "Leave application has been processed.",
      });
    },
    onError: (error: Error) => {
      toast({
        title: "Processing failed",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  const approveMessChangeMutation = useMutation({
    mutationFn: async ({ id, approved }: { id: string; approved: boolean }) => {
      const res = await apiRequest("POST", `/api/mess-changes/${id}/approve`, { approved });
      return res.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/mess-changes"] });
      toast({
        title: "Request processed",
        description: "Mess change request has been processed.",
      });
    },
    onError: (error: Error) => {
      toast({
        title: "Processing failed",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  const roomSwaps = roomSwapsData?.requests?.filter(r => r.status === "pending") || [];
  const leaveApps = leaveAppsData?.applications?.filter(a => a.status === "pending") || [];
  const messChanges = messChangesData?.requests?.filter(r => r.status === "pending") || [];

  const totalPending = roomSwaps.length + leaveApps.length + messChanges.length;

  return (
    <div className="max-w-7xl mx-auto p-6 space-y-6">
      <div>
        <h1 className="text-3xl font-bold">Approvals Management</h1>
        <p className="text-muted-foreground mt-1">
          Review and approve pending requests ({totalPending} pending)
        </p>
      </div>

      <Tabs defaultValue="room-swaps" className="space-y-6">
        <TabsList className="grid w-full max-w-2xl grid-cols-3">
          <TabsTrigger value="room-swaps">
            Room Swaps
            {roomSwaps.length > 0 && (
              <Badge variant="destructive" className="ml-2">
                {roomSwaps.length}
              </Badge>
            )}
          </TabsTrigger>
          <TabsTrigger value="leave-apps">
            Leave Applications
            {leaveApps.length > 0 && (
              <Badge variant="destructive" className="ml-2">
                {leaveApps.length}
              </Badge>
            )}
          </TabsTrigger>
          <TabsTrigger value="mess-changes">
            Mess Changes
            {messChanges.length > 0 && (
              <Badge variant="destructive" className="ml-2">
                {messChanges.length}
              </Badge>
            )}
          </TabsTrigger>
        </TabsList>

        <TabsContent value="room-swaps">
          <Card>
            <CardHeader>
              <CardTitle>Pending Room Swap Requests</CardTitle>
              <CardDescription>Review and approve room swap requests</CardDescription>
            </CardHeader>
            <CardContent>
              {roomSwapsLoading ? (
                <div className="flex items-center justify-center py-12">
                  <Loader2 className="w-8 h-8 animate-spin" />
                </div>
              ) : roomSwaps.length === 0 ? (
                <p className="text-center text-muted-foreground py-12">No pending requests</p>
              ) : (
                <Table>
                  <TableHeader>
                    <TableRow>
                      <TableHead>Requester</TableHead>
                      <TableHead>Target Student</TableHead>
                      <TableHead>Reason</TableHead>
                      <TableHead>Submitted</TableHead>
                      <TableHead>Actions</TableHead>
                    </TableRow>
                  </TableHeader>
                  <TableBody>
                    {roomSwaps.map((request) => (
                      <TableRow key={request.id}>
                        <TableCell className="font-medium">{request.requesterId}</TableCell>
                        <TableCell>{request.targetStudentId}</TableCell>
                        <TableCell className="max-w-xs truncate">{request.reason || "N/A"}</TableCell>
                        <TableCell>{formatDistanceToNow(new Date(request.createdAt), { addSuffix: true })}</TableCell>
                        <TableCell>
                          <div className="flex gap-2">
                            <Button
                              size="sm"
                              onClick={() => approveRoomSwapMutation.mutate({ id: request.id, approved: true })}
                              disabled={approveRoomSwapMutation.isPending}
                            >
                              <Check className="w-4 h-4 mr-1" />
                              Approve
                            </Button>
                            <Button
                              size="sm"
                              variant="destructive"
                              onClick={() => approveRoomSwapMutation.mutate({ id: request.id, approved: false })}
                              disabled={approveRoomSwapMutation.isPending}
                            >
                              <X className="w-4 h-4 mr-1" />
                              Reject
                            </Button>
                          </div>
                        </TableCell>
                      </TableRow>
                    ))}
                  </TableBody>
                </Table>
              )}
            </CardContent>
          </Card>
        </TabsContent>

        <TabsContent value="leave-apps">
          <Card>
            <CardHeader>
              <CardTitle>Pending Leave Applications</CardTitle>
              <CardDescription>Review and approve leave applications</CardDescription>
            </CardHeader>
            <CardContent>
              {leaveAppsLoading ? (
                <div className="flex items-center justify-center py-12">
                  <Loader2 className="w-8 h-8 animate-spin" />
                </div>
              ) : leaveApps.length === 0 ? (
                <p className="text-center text-muted-foreground py-12">No pending applications</p>
              ) : (
                <Table>
                  <TableHeader>
                    <TableRow>
                      <TableHead>Student</TableHead>
                      <TableHead>Duration</TableHead>
                      <TableHead>Destination</TableHead>
                      <TableHead>Reason</TableHead>
                      <TableHead>Submitted</TableHead>
                      <TableHead>Actions</TableHead>
                    </TableRow>
                  </TableHeader>
                  <TableBody>
                    {leaveApps.map((app) => (
                      <TableRow key={app.id}>
                        <TableCell className="font-medium">{app.studentId}</TableCell>
                        <TableCell>
                          {format(new Date(app.startDate), "MMM d")} - {format(new Date(app.endDate), "MMM d, yyyy")}
                        </TableCell>
                        <TableCell>{app.destination || "N/A"}</TableCell>
                        <TableCell className="max-w-xs truncate">{app.reason}</TableCell>
                        <TableCell>{formatDistanceToNow(new Date(app.createdAt), { addSuffix: true })}</TableCell>
                        <TableCell>
                          <div className="flex gap-2">
                            <Button
                              size="sm"
                              onClick={() => approveLeaveAppMutation.mutate({ id: app.id, approved: true })}
                              disabled={approveLeaveAppMutation.isPending}
                            >
                              <Check className="w-4 h-4 mr-1" />
                              Approve
                            </Button>
                            <Button
                              size="sm"
                              variant="destructive"
                              onClick={() => approveLeaveAppMutation.mutate({ id: app.id, approved: false })}
                              disabled={approveLeaveAppMutation.isPending}
                            >
                              <X className="w-4 h-4 mr-1" />
                              Reject
                            </Button>
                          </div>
                        </TableCell>
                      </TableRow>
                    ))}
                  </TableBody>
                </Table>
              )}
            </CardContent>
          </Card>
        </TabsContent>

        <TabsContent value="mess-changes">
          <Card>
            <CardHeader>
              <CardTitle>Pending Mess Change Requests</CardTitle>
              <CardDescription>Review and approve mess change requests</CardDescription>
            </CardHeader>
            <CardContent>
              {messChangesLoading ? (
                <div className="flex items-center justify-center py-12">
                  <Loader2 className="w-8 h-8 animate-spin" />
                </div>
              ) : messChanges.length === 0 ? (
                <p className="text-center text-muted-foreground py-12">No pending requests</p>
              ) : (
                <Table>
                  <TableHeader>
                    <TableRow>
                      <TableHead>Student</TableHead>
                      <TableHead>Current Mess</TableHead>
                      <TableHead>New Mess</TableHead>
                      <TableHead>Reason</TableHead>
                      <TableHead>Submitted</TableHead>
                      <TableHead>Actions</TableHead>
                    </TableRow>
                  </TableHeader>
                  <TableBody>
                    {messChanges.map((request) => (
                      <TableRow key={request.id}>
                        <TableCell className="font-medium">{request.studentId}</TableCell>
                        <TableCell>{request.currentMessId}</TableCell>
                        <TableCell>{request.newMessId}</TableCell>
                        <TableCell className="max-w-xs truncate">{request.reason || "N/A"}</TableCell>
                        <TableCell>{formatDistanceToNow(new Date(request.createdAt), { addSuffix: true })}</TableCell>
                        <TableCell>
                          <div className="flex gap-2">
                            <Button
                              size="sm"
                              onClick={() => approveMessChangeMutation.mutate({ id: request.id, approved: true })}
                              disabled={approveMessChangeMutation.isPending}
                            >
                              <Check className="w-4 h-4 mr-1" />
                              Approve
                            </Button>
                            <Button
                              size="sm"
                              variant="destructive"
                              onClick={() => approveMessChangeMutation.mutate({ id: request.id, approved: false })}
                              disabled={approveMessChangeMutation.isPending}
                            >
                              <X className="w-4 h-4 mr-1" />
                              Reject
                            </Button>
                          </div>
                        </TableCell>
                      </TableRow>
                    ))}
                  </TableBody>
                </Table>
              )}
            </CardContent>
          </Card>
        </TabsContent>
      </Tabs>
    </div>
  );
}

client/src/pages/LeaveApplication.tsx.content:
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { z } from "zod";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Textarea } from "@/components/ui/textarea";
import { Input } from "@/components/ui/input";
import { Badge } from "@/components/ui/badge";
import { Calendar } from "@/components/ui/calendar";
import { Popover, PopoverContent, PopoverTrigger } from "@/components/ui/popover";
import { Form, FormControl, FormField, FormItem, FormLabel, FormMessage } from "@/components/ui/form";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import { LeaveApplication } from "@shared/schema";
import { Loader2, Send, Calendar as CalendarIcon, FileText } from "lucide-react";
import { format, formatDistanceToNow } from "date-fns";
import { cn } from "@/lib/utils";

const leaveApplicationSchema = z.object({
  startDate: z.date({ required_error: "Start date is required" }),
  endDate: z.date({ required_error: "End date is required" }),
  reason: z.string().min(10, "Reason must be at least 10 characters"),
  destination: z.string().optional(),
}).refine(data => data.endDate >= data.startDate, {
  message: "End date must be after or equal to start date",
  path: ["endDate"],
});

type LeaveApplicationFormData = z.infer<typeof leaveApplicationSchema>;

export default function LeaveApplicationPage() {
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const { data: applicationsData, isLoading } = useQuery<{ applications: LeaveApplication[] }>({
    queryKey: ["/api/leave-applications"],
  });

  const form = useForm<LeaveApplicationFormData>({
    resolver: zodResolver(leaveApplicationSchema),
    defaultValues: {
      reason: "",
      destination: "",
    },
  });

  const createMutation = useMutation({
    mutationFn: async (data: LeaveApplicationFormData) => {
      const res = await apiRequest("POST", "/api/leave-applications", data);
      return res.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/leave-applications"] });
      form.reset();
      toast({
        title: "Application submitted",
        description: "Your leave application has been submitted successfully.",
      });
    },
    onError: (error: Error) => {
      toast({
        title: "Submission failed",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  const onSubmit = (data: LeaveApplicationFormData) => {
    createMutation.mutate(data);
  };

  const applications = applicationsData?.applications || [];
  const pendingApplications = applications.filter(a => a.status === "pending");
  const approvedApplications = applications.filter(a => a.status === "approved");
  const rejectedApplications = applications.filter(a => a.status === "rejected");

  const getStatusBadge = (status: string) => {
    if (status === "pending") return <Badge variant="outline">Pending</Badge>;
    if (status === "approved") return <Badge variant="default">Approved</Badge>;
    return <Badge variant="destructive">Rejected</Badge>;
  };

  return (
    <div className="max-w-4xl mx-auto p-6 space-y-6">
      <div>
        <h1 className="text-3xl font-bold">Leave Application</h1>
        <p className="text-muted-foreground mt-1">Apply for leave from hostel</p>
      </div>

      <Card>
        <CardHeader>
          <CardTitle>Submit New Application</CardTitle>
          <CardDescription>Fill in the details for your leave request</CardDescription>
        </CardHeader>
        <CardContent>
          <Form {...form}>
            <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-4">
              <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                <FormField
                  control={form.control}
                  name="startDate"
                  render={({ field }) => (
                    <FormItem className="flex flex-col">
                      <FormLabel>Start Date</FormLabel>
                      <Popover>
                        <PopoverTrigger asChild>
                          <FormControl>
                            <Button
                              variant="outline"
                              className={cn(
                                "w-full pl-3 text-left font-normal",
                                !field.value && "text-muted-foreground"
                              )}
                            >
                              {field.value ? (
                                format(field.value, "PPP")
                              ) : (
                                <span>Pick a date</span>
                              )}
                              <CalendarIcon className="ml-auto h-4 w-4 opacity-50" />
                            </Button>
                          </FormControl>
                        </PopoverTrigger>
                        <PopoverContent className="w-auto p-0" align="start">
                          <Calendar
                            mode="single"
                            selected={field.value}
                            onSelect={field.onChange}
                            disabled={(date) => date < new Date()}
                            initialFocus
                          />
                        </PopoverContent>
                      </Popover>
                      <FormMessage />
                    </FormItem>
                  )}
                />

                <FormField
                  control={form.control}
                  name="endDate"
                  render={({ field }) => (
                    <FormItem className="flex flex-col">
                      <FormLabel>End Date</FormLabel>
                      <Popover>
                        <PopoverTrigger asChild>
                          <FormControl>
                            <Button
                              variant="outline"
                              className={cn(
                                "w-full pl-3 text-left font-normal",
                                !field.value && "text-muted-foreground"
                              )}
                            >
                              {field.value ? (
                                format(field.value, "PPP")
                              ) : (
                                <span>Pick a date</span>
                              )}
                              <CalendarIcon className="ml-auto h-4 w-4 opacity-50" />
                            </Button>
                          </FormControl>
                        </PopoverTrigger>
                        <PopoverContent className="w-auto p-0" align="start">
                          <Calendar
                            mode="single"
                            selected={field.value}
                            onSelect={field.onChange}
                            disabled={(date) => date < new Date()}
                            initialFocus
                          />
                        </PopoverContent>
                      </Popover>
                      <FormMessage />
                    </FormItem>
                  )}
                />
              </div>

              <FormField
                control={form.control}
                name="destination"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>Destination (Optional)</FormLabel>
                    <FormControl>
                      <Input
                        placeholder="Where are you going?"
                        {...field}
                        disabled={createMutation.isPending}
                      />
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />

              <FormField
                control={form.control}
                name="reason"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>Reason</FormLabel>
                    <FormControl>
                      <Textarea
                        placeholder="Explain the reason for your leave..."
                        {...field}
                        disabled={createMutation.isPending}
                        rows={4}
                      />
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />

              <Button type="submit" disabled={createMutation.isPending}>
                {createMutation.isPending ? (
                  <Loader2 className="w-4 h-4 mr-2 animate-spin" />
                ) : (
                  <Send className="w-4 h-4 mr-2" />
                )}
                Submit Application
              </Button>
            </form>
          </Form>
        </CardContent>
      </Card>

      {isLoading ? (
        <div className="flex items-center justify-center py-12">
          <Loader2 className="w-8 h-8 animate-spin" />
        </div>
      ) : (
        <>
          {pendingApplications.length > 0 && (
            <Card>
              <CardHeader>
                <CardTitle>Pending Applications</CardTitle>
                <CardDescription>Applications awaiting approval</CardDescription>
              </CardHeader>
              <CardContent className="space-y-4">
                {pendingApplications.map((app) => (
                  <div key={app.id} className="border rounded-lg p-4">
                    <div className="flex items-center justify-between mb-2">
                      <div className="flex items-center gap-2">
                        <FileText className="w-4 h-4" />
                        <span className="font-medium">
                          {format(new Date(app.startDate), "PPP")} - {format(new Date(app.endDate), "PPP")}
                        </span>
                      </div>
                      {getStatusBadge(app.status)}
                    </div>
                    <p className="text-sm text-muted-foreground mb-2">{app.reason}</p>
                    {app.destination && (
                      <p className="text-sm text-muted-foreground mb-2">Destination: {app.destination}</p>
                    )}
                    <p className="text-xs text-muted-foreground">
                      Submitted {formatDistanceToNow(new Date(app.createdAt), { addSuffix: true })}
                    </p>
                  </div>
                ))}
              </CardContent>
            </Card>
          )}

          {approvedApplications.length > 0 && (
            <Card>
              <CardHeader>
                <CardTitle>Approved Applications</CardTitle>
                <CardDescription>Your approved leave requests</CardDescription>
              </CardHeader>
              <CardContent className="space-y-4">
                {approvedApplications.map((app) => (
                  <div key={app.id} className="border rounded-lg p-4 bg-green-50 dark:bg-green-950">
                    <div className="flex items-center justify-between mb-2">
                      <div className="flex items-center gap-2">
                        <FileText className="w-4 h-4" />
                        <span className="font-medium">
                          {format(new Date(app.startDate), "PPP")} - {format(new Date(app.endDate), "PPP")}
                        </span>
                      </div>
                      {getStatusBadge(app.status)}
                    </div>
                    <p className="text-sm text-muted-foreground mb-2">{app.reason}</p>
                    {app.destination && (
                      <p className="text-sm text-muted-foreground mb-2">Destination: {app.destination}</p>
                    )}
                  </div>
                ))}
              </CardContent>
            </Card>
          )}

          {rejectedApplications.length > 0 && (
            <Card>
              <CardHeader>
                <CardTitle>Rejected Applications</CardTitle>
                <CardDescription>Applications that were not approved</CardDescription>
              </CardHeader>
              <CardContent className="space-y-4">
                {rejectedApplications.map((app) => (
                  <div key={app.id} className="border rounded-lg p-4 opacity-60">
                    <div className="flex items-center justify-between mb-2">
                      <div className="flex items-center gap-2">
                        <FileText className="w-4 h-4" />
                        <span className="font-medium">
                          {format(new Date(app.startDate), "PPP")} - {format(new Date(app.endDate), "PPP")}
                        </span>
                      </div>
                      {getStatusBadge(app.status)}
                    </div>
                    <p className="text-sm text-muted-foreground mb-2">{app.reason}</p>
                  </div>
                ))}
              </CardContent>
            </Card>
          )}
        </>
      )}
    </div>
  );
}

